import React, { useState, useEffect } from "react";

import { AppointmentForm } from "../../components/appointmentForm/AppointmentForm";
import { TileList } from "../../components/tileList/TileList";

export const AppointmentsPage = ({allContacts, allAppointments, newAppointiment}) => {
  /*
  Define state variables for 
  appointment info
  */
  const [title, setTitle] = useState("");
  const [contact, setContact] = useState("");
  const [date, setDate] = useState("");
  const [time, setTime] = useState("");

  const [isValid, setIsValid] = useState(false)
  useEffect(() => {
    const contactExists = allContacts.some(contact => contact.name === contact)
    if (contactExists) {
      setIsValid(true);
    } else {
      setIsValid(false);
    }
  }, [contact])


  const handleSubmit = (e) => {
    e.preventDefault();
    if (isValid) {
      newAppointiment({title, contact, date, time});
      setTitle("");
      setContact("");
      setDate("");
      setTime("");
    }
  };

  return (
    <div>
      <section>
        <h2>Add Appointment</h2>
        <AppointmentForm 
          title={title} 
          setTitle={setTitle} 
          contact={contact} 
          setContact={setContact} 
          date={date} 
          setDate={setDate}
          time={time}
          setTime={setTime}
          handleSubmit={handleSubmit}
          isInvalid={!isValid}
        />
      </section>
      <hr />
      <section>
        <h2>Appointments</h2>
      </section>
    </div>
  );
};
